import React from 'react';
import styled from 'styled-components';

function Details() {
  return <Container>
      <Background>
        <img src="https://prod-ripcut-delivery.disney-plus.net/v1/variant/disney/9D8AEB7DE234898392BFD20E7D9B112B841E920AF9A3F54CCFB966722AFF3461/scale?width=1920&aspectRatio=1.78&format=jpeg" />
      </Background>
        <ImageTitle>
          <img src="/images/logo.svg" />
        </ImageTitle>
        <Controls>
          <PlayButton></PlayButton>
          <TrailerButton></TrailerButton>
          <AddButton></AddButton>
          <GroupWatchButton></GroupWatchButton>
        </Controls>
  </Container>;
}

export default Details;

const Container = styled.div`
  min-height: calc(100vh - 70px);
  padding: 0 calc(3.5vw + 5px);
  position: relative;
`

const Background = styled.div`
    position: fixed;
    top: 0;
    left: 0;
    bottom: 0;
    right: 0;
    z-index: -1;
    opacity: 0.8;

    img {
      width: 100%;
      height: 100%;
      object-fit: cover;
    }
`

const ImageTitle = styled.div`
   height: 30vh;
   min-height: 170px;
   width: 35vw;
   min-width: 200px;
   img {
     width: 100%;
     height: 100%;
     object-fit: contain;
   }
`

const Controls = styled.div``

const PlayButton = styled.button``

const TrailerButton = styled.button``

const AddButton = styled.button``

const GroupWatchButton = styled.button``
